// SECTION 1: Extract email data
const items = $input.all();
const results = [];

for (const item of items) {
  const emailData = item.json;
  const subject = emailData.subject || '';
  const body = (emailData.textPlain || emailData.snippet || '').toLowerCase();
  const from = emailData.from || '';
  const senderEmail = from.match(/[\w.-]+@[\w.-]+/)?.[0] || '';
  const senderDomain = senderEmail.split('@')[1] || '';

  // Extract name, phone, address from email body
  const phoneMatch = body.match(/\b\d{3}[-.]?\d{3}[-.]?\d{4}\b/);
  const zipMatch = body.match(/\b\d{5}(-\d{4})?\b/);

  // SECTION 2: Lead Type Classification (Residential vs Commercial)
  let leadType = 'Residential';
  let leadTypeConfidence = 'low';

  // Check for commercial indicators
  const commercialDomains = ['corp', 'inc', 'llc', 'company', 'properties', 'management'];
  const commercialKeywords = ['office', 'building', 'facility', 'commercial', 'business', 'retail', 'warehouse', 'property management', 'multiple units', 'our building', 'our facility'];
  const commercialTitles = ['property manager', 'facility director', 'building owner', 'general manager', 'operations manager', 'facility manager'];

  const isCommercialDomain = commercialDomains.some(term => senderDomain.includes(term));
  const hasCommercialKeywords = commercialKeywords.some(keyword => body.includes(keyword));
  const hasCommercialTitle = commercialTitles.some(title => body.includes(title));

  // Consumer email domains (strong residential indicators)
  const consumerDomains = ['gmail.com', 'yahoo.com', 'aol.com', 'hotmail.com', 'outlook.com', 'icloud.com'];
  const isConsumerDomain = consumerDomains.includes(senderDomain);

  if (isCommercialDomain || hasCommercialKeywords || hasCommercialTitle) {
    leadType = 'Commercial';
    leadTypeConfidence = 'high';
  } else if (isConsumerDomain) {
    leadType = 'Residential';
    leadTypeConfidence = 'high';
  }

  results.push({
    json: {
      // Original email data
      originalEmail: emailData,
      subject: subject,
      body: body,
      from: from,
      senderEmail: senderEmail,
      senderDomain: senderDomain,
      receivedAt: new Date().toISOString(),

      // Parsed contact info
      phone: phoneMatch?.[0] || '',
      zipCode: zipMatch?.[0] || '',

      // Classification
      leadType: leadType,
      leadTypeConfidence: leadTypeConfidence,

      // Prepare for scoring
      bodyLower: body,
      subjectLower: subject.toLowerCase()
    }
  });
}

return results;

// HVAC BANT SCORING MATRIX
const items = $input.all();
const results = [];

for (const item of items) {
  let score = 0;
  const data = item.json;
  const body = data.bodyLower;
  const subject = data.subjectLower;
  const leadType = data.leadType;
  const zipCode = data.zipCode;

  // Track scoring breakdown for transparency
  const scoreBreakdown = {};

  // URGENCY SCORING (30 points max)
  const highUrgencyKeywords = ['urgent', 'emergency', 'asap', 'need now', 'broken', 'not working', 'no heat', 'no cool', 'leaking', 'stopped', 'completely stopped'];
  const moderateUrgencyKeywords = ['soon', 'this week', 'schedule', 'before', 'need help'];

  if (highUrgencyKeywords.some(kw => body.includes(kw) || subject.includes(kw))) {
    score += 30;
    scoreBreakdown.urgency = { points: 30, level: 'High' };
  } else if (moderateUrgencyKeywords.some(kw => body.includes(kw) || subject.includes(kw))) {
    score += 15;
    scoreBreakdown.urgency = { points: 15, level: 'Moderate' };
  } else {
    scoreBreakdown.urgency = { points: 0, level: 'Low' };
  }

  // SERVICE SPECIFICITY (20 points max)
  const specificServiceKeywords = ['quote for', 'install', 'replace', 'replacement', 'fix my', 'repair my', 'service my', 'furnace', 'ac unit', 'hvac system', 'air conditioner', 'heat pump', 'thermostat'];
  const generalInquiryKeywords = ['question about', 'information on', 'how much'];

  if (specificServiceKeywords.some(kw => body.includes(kw))) {
    score += 20;
    scoreBreakdown.serviceSpecificity = { points: 20, type: 'Specific' };
  } else if (generalInquiryKeywords.some(kw => body.includes(kw))) {
    score += 5;
    scoreBreakdown.serviceSpecificity = { points: 5, type: 'General' };
  } else {
    scoreBreakdown.serviceSpecificity = { points: 0, type: 'Unclear' };
  }

  // AUTHORITY SCORING
  if (leadType === 'Residential') {
    // Homeowner assumed to have authority
    score += 5;
    scoreBreakdown.authority = { points: 5, type: 'Homeowner (Implicit)' };
  } else if (leadType === 'Commercial') {
    const authorityTitles = ['property manager', 'facility director', 'owner', 'general manager', 'building manager', 'operations manager'];
    if (authorityTitles.some(title => body.includes(title))) {
      score += 15;
      scoreBreakdown.authority = { points: 15, type: 'Decision Maker' };
    } else {
      scoreBreakdown.authority = { points: 0, type: 'Unknown' };
    }
  }

  // BUDGET INDICATORS (20 points max)
  const financingKeywords = ['financing', 'payment plan', 'monthly payments'];
  const budgetMentioned = body.match(/budget.*\$/) || body.match(/\$[\d,]+/);

  if (budgetMentioned) {
    const budgetPoints = leadType === 'Commercial' ? 20 : 15;
    score += budgetPoints;
    scoreBreakdown.budget = { points: budgetPoints, type: 'Explicit Budget' };
  } else if (financingKeywords.some(kw => body.includes(kw))) {
    score += 10;
    scoreBreakdown.budget = { points: 10, type: 'Financing Inquiry' };
  } else {
    scoreBreakdown.budget = { points: 0, type: 'Not Mentioned' };
  }

  // CONTACT COMPLETENESS (10 points)
  const hasPhone = data.phone !== '';
  const hasZip = data.zipCode !== '';

  if (hasPhone && hasZip) {
    score += 10;
    scoreBreakdown.contactInfo = { points: 10, status: 'Complete' };
  } else if (hasPhone || hasZip) {
    score += 5;
    scoreBreakdown.contactInfo = { points: 5, status: 'Partial' };
  } else {
    scoreBreakdown.contactInfo = { points: 0, status: 'Incomplete' };
  }

  // PAIN POINTS (15 points)
  const painPointKeywords = ['old system', 'high bills', 'energy costs', 'frequent repairs', 'noisy', 'loud', 'inefficient', 'breaking down'];
  if (painPointKeywords.some(kw => body.includes(kw))) {
    score += 15;
    scoreBreakdown.painPoints = { points: 15, detected: true };
  } else {
    scoreBreakdown.painPoints = { points: 0, detected: false };
  }

  // LEAD TYPE BONUS (Commercial)
  if (leadType === 'Commercial') {
    score += 15;
    scoreBreakdown.leadTypeBonus = { points: 15, type: 'Commercial' };
  } else {
    scoreBreakdown.leadTypeBonus = { points: 0, type: 'Residential' };
  }

  // NEGATIVE SCORING
  // Out of service area check
  const serviceAreaZips = ['90001', '90002', '90003', '90004', '90005']; // REPLACE WITH ACTUAL SERVICE AREA
  if (zipCode && !serviceAreaZips.includes(zipCode)) {
    score -= 50;
    scoreBreakdown.negativeFactors = { points: -50, reason: 'Out of Service Area' };
  }

  // Non-HVAC service check
  const nonHVACKeywords = ['plumbing', 'plumber', 'electrical', 'electrician', 'roofing', 'roofer'];
  if (nonHVACKeywords.some(kw => body.includes(kw))) {
    score -= 50;
    scoreBreakdown.negativeFactors = { points: -50, reason: 'Non-HVAC Service' };
  }

  // DETERMINE QUALIFICATION TIER
  let qualificationTier = '';
  let qualificationLabel = '';

  if (score > 60) {
    qualificationTier = 'SQL';
    qualificationLabel = 'Hot Lead - Sales Qualified';
  } else if (score >= 31 && score <= 60) {
    qualificationTier = 'MQL';
    qualificationLabel = 'Warm Lead - Marketing Qualified';
  } else {
    qualificationTier = 'Cold';
    qualificationLabel = 'Cold Lead - Unqualified';
  }

  results.push({
    json: {
      ...data,
      leadScore: score,
      scoreBreakdown: scoreBreakdown,
      qualificationTier: qualificationTier,
      qualificationLabel: qualificationLabel,
      scoredAt: new Date().toISOString()
    }
  });
}

return results;

