// Check disqualification reasons for cold leads
const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;
  let disqualificationReason = '';
  let actionPath = '';

  // Check score breakdown for negative factors
  if (data.scoreBreakdown.negativeFactors) {
    disqualificationReason = data.scoreBreakdown.negativeFactors.reason;

    if (disqualificationReason === 'Out of Service Area') {
      actionPath = 'referral';
    } else if (disqualificationReason === 'Non-HVAC Service') {
      actionPath = 'referral';
    } else {
      actionPath = 'long-term-nurture';
    }
  } else {
    // Low score but no hard disqualification
    disqualificationReason = 'Low engagement score';
    actionPath = 'long-term-nurture';
  }

  results.push({
    json: {
      ...data,
      disqualificationReason: disqualificationReason,
      actionPath: actionPath
    }
  });
}

return results;
